mapper.select:
SELECT t0.material_code FROM dwk_pass_box_real t0 WHERE t0.is_delete = 0 AND t0.storage_location_code in
#{code}
GROUP BY t0.material_code

SELECT t0.resource_code FROM dwk_resource_binding t0 LEFT JOIN dwk_resource t1 ON t1.id = t0.binding_resource_id LEFT JOIN dwk_resource t2 ON t2.id = t0.resource_id WHERE t1.CODE = 'warehouse' AND t2.CODE = 'storageLocation' AND t0.binding_resource_code in
#{code}
AND t0.is_delete = 0

SELECT t0.resource_code FROM dwk_resource_binding t0 LEFT JOIN dwk_resource t1 ON t1.id = t0.binding_resource_id LEFT JOIN dwk_resource t2 ON t2.id = t0.resource_id WHERE t1.CODE = 'area' AND t2.CODE = 'warehouse' AND t0.binding_resource_code in
#{code}
AND t0.is_delete = 0

SELECT t1.pass_box_code FROM (SELECT t0.pass_box_code, t0.material_code FROM dwk_pass_box_real t0 WHERE t0.is_delete = 0 AND t0.storage_location_code IN
#{positionCode}
) AS t1
WHERE t1.material_code IN
#{materialCode}

SELECT t0.resource_code FROM dwk_resource_binding t0 LEFT JOIN dwk_resource t1 ON t1.id = t0.binding_resource_id LEFT JOIN dwk_resource t2 ON t2.id = t0.resource_id WHERE t1.CODE = 'area' AND t2.CODE = 'storageLocation' AND t0.binding_resource_code in
#{code}
AND t0.is_delete = 0

SELECT t3.`code`, t3.pass_box_code as passBoxCode FROM ( SELECT t0.pass_box_code, t2.`code` FROM u_inventory_sheet_detail AS t0 LEFT JOIN u_inventory_sheet_plan AS t1 ON t0.inventory_sheet_d_id = t1.id LEFT JOIN u_inventory_sheet AS t2 ON t1.inventory_sheet_p_id = t2.id WHERE t2.`status` IN ('unIssued', 'unClear') AND t0.is_delete = 0 AND t1.is_delete = 0 AND t2.is_delete = 0 ) AS t3 WHERE t3.pass_box_code IN
#{code}
and t3.code != #{inventoryCode}
limit 1;

SELECT
t1.id,
t1.logistics_status
FROM dwk_logistics_custom_gn1414 AS t1
LEFT JOIN dwk_logistics_custom_gn1414_ext AS t2 ON t1.id = t2.ext_GN1414_id
WHERE t1.is_delete = 0
AND t2.is_delete = 0

    AND t2.ext_inventory_material_code = #{inventoryMaterialCode}


    AND t2.ext_inventory_area_code = #{inventoryAreaCode}


    AND t2.ext_inventory_warehouse_code = #{inventoryWarehouseCode}


    AND t2.ext_inventory_position_code = #{inventoryPositionCode}

AND t2.ext_code = #{inventorySheetCode}

SELECT
t1.id,
t1.logistics_status
FROM dwk_logistics_custom_gn3233 AS t1
LEFT JOIN dwk_logistics_custom_gn3233_ext AS t2 ON t1.id = t2.ext_GN3233_id
WHERE t1.is_delete = 0
AND t2.is_delete = 0

    AND t2.ext_inventory_material_code = #{inventoryMaterialCode}


    AND t2.ext_inventory_area_code = #{inventoryAreaCode}


    AND t2.ext_inventory_warehouse_code = #{inventoryWarehouseCode}


    AND t2.ext_inventory_position_code = #{inventoryPositionCode}

AND t2.ext_code = #{inventorySheetCode}
